import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator, TransitionPresets } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { useAuth } from '../contexts/AuthContext';

import LoginScreen from '../screens/LoginScreen';
import RegisterScreen from '../screens/RegisterScreen';
import ProductsScreen from '../screens/ProductsScreen';
import CartScreen from '../screens/CartScreen';
import CheckoutScreen from '../screens/CheckoutScreen';
import DashboardScreen from '../screens/DashboardScreen';
import ComprasScreen from '../screens/ComprasScreen';
import ReporteriaScreen from '../screens/ReporteriaScreen';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

// ðŸ§  Estilos tipados correctamente para evitar errores con TypeScript
const styles = StyleSheet.create({
  headerTitle: {
    fontWeight: '700',
    fontSize: 20,
    letterSpacing: 0.5,
  },
});

const commonScreenOptions = {
  headerBackground: () => (
    <LinearGradient
      colors={['#3498db', '#2980b9']}
      style={StyleSheet.absoluteFill}
      start={{ x: 0, y: 0 }}
      end={{ x: 1, y: 1 }}
    />
  ),
  headerTintColor: '#fff',
  headerTitleStyle: styles.headerTitle, // âœ… Ya no da error aquÃ­
  headerShadowVisible: false,
  ...TransitionPresets.SlideFromRightIOS,
};

// ðŸ§­ Tabs para usuarios autenticados
function MainTabs() {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        headerShown: true,
        tabBarShowLabel: true,
        tabBarActiveTintColor: '#3498db',
        tabBarInactiveTintColor: '#7f8c8d',
        tabBarStyle: {
          paddingBottom: 4,
          height: 60,
        },
        tabBarIcon: ({ color, size }) => {
          let iconName: keyof typeof Ionicons.glyphMap;

          switch (route.name) {
            case 'Dashboard':
              iconName = 'home-outline';
              break;
            case 'Compras':
              iconName = 'cart-outline';
              break;
            case 'ReporterÃ­a':
              iconName = 'bar-chart-outline';
              break;
            case 'Productos':
              iconName = 'list-outline';
              break;
            case 'Carrito':
              iconName = 'basket-outline';
              break;
            default:
              iconName = 'ellipse-outline';
          }

          return <Ionicons name={iconName} size={size} color={color} />;
        },
      })}
    >
      <Tab.Screen
        name="Dashboard"
        component={DashboardScreen}
        options={{ title: 'Inicio' }}
      />
      <Tab.Screen name="Compras" component={ComprasScreen} />
      <Tab.Screen name="ReporterÃ­a" component={ReporteriaScreen} />
      <Tab.Screen name="Productos" component={ProductsScreen} />
      <Tab.Screen name="Carrito" component={CartScreen} />
    </Tab.Navigator>
  );
}

export default function AppNavigator() {
  const { user } = useAuth();

  return (
    <NavigationContainer>
      <Stack.Navigator screenOptions={commonScreenOptions}>
        {user ? (
          <>
            <Stack.Screen
              name="MainTabs"
              component={MainTabs}
              options={{ headerShown: false }}
            />
            <Stack.Screen
              name="Checkout"
              component={CheckoutScreen}
              options={{
                title: 'ðŸ’³ Finalizar Compra',
                headerBackTitle: 'Carrito',
                gestureEnabled: false,
              }}
            />
          </>
        ) : (
          <>
            <Stack.Screen
              name="Login"
              component={LoginScreen}
              options={{
                headerShown: false,
                animationTypeForReplace: user ? 'pop' : 'push',
              }}
            />
            <Stack.Screen
              name="Register"
              component={RegisterScreen}
              options={{ headerShown: false }}
            />
          </>
        )}
      </Stack.Navigator>
    </NavigationContainer>
  );
}
